import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../style/navbar.css";

export const Navbar = ({ active, updateCurrentPage }) => {
  const [currentPage, setCurrentPage] = useState(active);

  useEffect(()=>{
    setCurrentPage(window.location.pathname)
    console.log(currentPage)
  },[]);
  return (
    <nav className="nav">
      <div className="nav__groups">
        <div className="nav__left">
          <div className="nav__logo">
            <div className="logo">
              <Link
              className="logo__link"
              to={'/home'}>
                B
              </Link>
            </div>
          </div>
          <div className="nav__name">Bekzot A.</div>
        </div>
        <div className="nav__right">
          <div className="nav__link">
            <Link
              to={"/home"}
              onClick={() => {
                updateCurrentPage("/home");
              }}
              className={currentPage === "/home" ? "active " + "link" : "link"}
            >
              Home
            </Link>
          </div>
          <div className="nav__link">
            <Link
              to="/resume"
              onClick={() => {
                updateCurrentPage("/resume");
              }}
              className={currentPage === "/resume" ? "active " + "link" : "link"}
            >
              Resume
            </Link>
          </div>
          <div className="nav__link">
            <Link
              to={"/projects"}
              onClick={() => {
                updateCurrentPage("/projects");
              }}
              className={currentPage === "/projects" ? "active " + "link" : "link"}
            >
              Projects
            </Link>
          </div>
          <div className="nav__link">
            <Link
              to={"/contact"}
              onClick={() => {
                updateCurrentPage("/contact");
              }}
              className={currentPage === "/contact" ? "active " + "link" : "link"}
            >
              Contact
            </Link>
          </div>
        </div>
      </div>
    </nav>
  );
};
